import React, { useState } from 'react';

function Home() {
  
  const initialPosts = [
    { 
      id: 1, 
      title: 'Travel the world', 
      imageUrl: 'data:image/jpeg;base64,' 
    },
    { 
      id: 2, 
      title: 'Artttttt!!!', 
    
      imageUrl: 'data:image/jpeg;base64,' 
    },
    { 
      id: 3, 
      title: 'Food is ART', 
      imageUrl: 'data:image/jpeg;base64,'
    },
  ];

  // State to hold posts
  const [posts, setPosts] = useState(initialPosts);

  // Handle Delete
  const handleDelete = (id) => {
    setPosts(posts.filter(post => post.id !== id));
  };

  // Handle Update
  const handleUpdate = (id) => {
    const updatedTitle = prompt("Enter the new title:");
    const updatedBody = prompt("Enter the new body:");

    if (updatedTitle && updatedBody) {
      setPosts(posts.map(post => 
        post.id === id ? { ...post, title: updatedTitle, body: updatedBody } : post
      ));
    }
  };

  return (
    <div style={styles.container}>
      <h1>Blog Posts</h1>
      <div style={styles.cardContainer}>
        {posts.map((post) => (
          <div key={post.id} style={styles.card}>
            <img 
              src={post.imageUrl} 
              alt={post.title} 
              style={styles.cardImage} 
            />
            <h2 style={styles.cardTitle}>{post.title}</h2>
            <p>{post.body}</p>
            <div style={styles.buttons}>
              <button 
                onClick={() => handleUpdate(post.id)} 
                style={styles.button}>Update</button>
              <button 
                onClick={() => handleDelete(post.id)} 
                style={styles.button}>Delete</button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

const styles = {
  container: {
    padding: '100px',
    textAlign: 'center',
    backgroundColor: '#ffffff', 
  },
  cardContainer: {
    display: 'flex',
    justifyContent: 'space-around',
    gap: '20px',
    flexWrap: 'wrap',
    marginTop: '20px',
  },
  card: {
    backgroundColor: '#fff',
    padding: '20px',
    borderRadius: '8px',
    boxShadow: '0 2px 8px rgba(0, 0, 0, 0.1)',
    width: '250px',
    textAlign: 'left',
  },
  cardTitle: {
    color:'black',
    marginBottom: '10px',
  },
  cardBody: {
    color: '#000',  // Change body text color to black
    marginBottom: '10px',
  },
  cardImage: {
    width: '100%',
    borderRadius: '8px',
    marginBottom: '10px',
  },
  buttons: {
    display: 'flex',
    gap: '10px',
    marginTop: '10px',
  },
  button: {
    padding: '10px',
    borderRadius: '5px',
    border: 'none',
    backgroundColor: '#0073e6',
    color: '#fff',
    cursor: 'pointer',
  },
};

export default Home;
